namespace Geissweb\Euvat\Block\System\Config\Field;

class PriceDisplayType extends \Magento\Framework\View\Element\Html\Select
{
    /**
     * @var \Magento\Tax\Model\System\Config\Source\Tax\Display\Type
     */
    public $priceDisplayTypes;

    /**
     * PriceDisplayType constructor.
     *
     * @param \Magento\Framework\View\Element\Context                  $context
     * @param \Magento\Tax\Model\System\Config\Source\Tax\Display\Type $priceDisplayTypes
     * @param array                                                    $data
     */
    public function __construct(
        \Magento\Framework\View\Element\Context $context,
        \Magento\Tax\Model\System\Config\Source\Tax\Display\Type $priceDisplayTypes,
        array $data = []
    ) {
        parent::__construct($context, $data);
        $this->priceDisplayTypes = $priceDisplayTypes;
    }

    /**
     * @param string $value
     * @return $this
     */
    public function setInputName($value)
    {
        return $this->setName($value);
    }

    /**
     * Render block HTML
     *
     * @return string
     */
    public function _toHtml()
    {
        if (!$this->getOptions()) {
            $options = $this->priceDisplayTypes->toOptionArray();
            foreach ($options as $option) {
                $this->addOption($option['value'], addslashes($option['label']));
            }
        }
        return parent::_toHtml();
    }
}
