namespace Geissweb\Euvat\Block;

use Magento\Framework\View\Element\Template;
use Magento\Framework\View\Element\Template\Context;

/**
 * @property array layoutProcessors
 */
class Vatfield extends Template
{
    /**
     * @var \Geissweb\Euvat\Helper\Configuration
     */
    private $config;

    /**
     * Constructor
     *
     * @param Context                              $context
     * @param \Geissweb\Euvat\Helper\Configuration $config
     * @param array                                $layoutProcessors
     * @param array                                $data
     */
    public function __construct(
        Context $context,
        \Geissweb\Euvat\Helper\Configuration $config,
        $layoutProcessors = [],
        $data = []
    ) {
        parent::__construct($context, $data);

        $this->config = $config;
        $this->layoutProcessors = $layoutProcessors;
    }

    /**
     * Get JS Layout
     * @return string
     */
    public function getJsLayout()
    {
        foreach ($this->layoutProcessors as $processor) {
            $this->jsLayout = $processor->process($this->jsLayout);
        }
        return parent::getJsLayout();
    }

    /**
     * Gets system config
     * @return \Geissweb\Euvat\Helper\Configuration
     */
    public function getConfigHelper()
    {
        return $this->config;
    }
}
