namespace Geissweb\Euvat\Controller\Adminhtml\Setup;

/**
 * Class Setup
 * @package Geissweb\Euvat\Controller\Adminhtml\Setup
 */
class Setup extends \Magento\Backend\App\Action
{
    /**
     * Authorization level of a basic admin session
     *
     * @see _isAllowed()
     */
    const ADMIN_RESOURCE = 'Geissweb_Euvat::setup';

    /**
     * @var \Geissweb\Euvat\Model\Setup\Tax
     */
    private $euvatSetupTax;

    /**
     * @var \Magento\Backend\App\Action\Context
     */
    private $context;

    /**
     * Initialize Group Controller
     *
     * @param \Magento\Backend\App\Action\Context $context
     * @param \Geissweb\Euvat\Model\Setup\Tax     $euvatSetupTax
     * @param \Magento\Framework\Message\Manager  $messageManager
     *
     * @internal param \Magento\Framework\View\Result\PageFactory $resultPageFactory
     */
    public function __construct(
        \Magento\Backend\App\Action\Context $context,
        \Geissweb\Euvat\Model\Setup\Tax $euvatSetupTax,
        \Magento\Framework\Message\Manager $messageManager
    ) {
        $this->euvatSetupTax  = $euvatSetupTax;
        $this->messageManager = $messageManager;
        $this->context        = $context;
        parent::__construct($context);
    }

    /**
     * @return \Magento\Framework\Controller\Result\Redirect
     */
    public function execute()
    {
        try {
            $resultRedirect = $this->resultRedirectFactory->create();
            $resultRedirect->setPath('*/*');
            $post = $this->getRequest()->getParams();

            if (empty($post)) {
                $this->messageManager->addNotice(__('Can not execute the Setup without data.'));

            } else {
                /** @var \Geissweb\Euvat\Model\Setup\Tax $setup */
                $setup = $this->euvatSetupTax->runSetup($post['general']);
                if ($setup) {
                    $this->euvatSetupTax->clearConfigCache();
                    $this->messageManager->addSuccessMessage(__('EU VAT Enhanced Setup completed successfully. Please now apply your settings.'));
                    $resultRedirect->setPath('adminhtml/system_config/edit/', ['section' => 'euvat']);
                }
            }
            return $resultRedirect;

        } catch (\Exception $e) {
            $this->messageManager->addException($e, $e->getMessage());
        }
    }

    /**
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed(self::ADMIN_RESOURCE);
    }
}
