namespace Geissweb\Euvat\Logger;
use DateTimeZone;
use Monolog\Handler\StreamHandler;
use Magento\Store\Model\ScopeInterface;

/**
 * Class Logger
 * @package Geissweb\Euvat\Logger
 */
class Logger extends \Monolog\Logger
{
    /**
     * @var \Magento\Framework\App\Config\ScopeConfigInterface
     */
    public $scopeConfig;
 /**
     * @var DateTimeZone
     */
    protected $timezone;
    /**
     * @param string $name The logging channel
     * @param HandlerInterface[] $handlers Optional stack of handlers, the first one in the array is called first, etc.
     * @param \Magento\Framework\App\Config\ScopeConfigInterface $scopeConfig
     */
    public function __construct(
        \Magento\Framework\App\Config\ScopeConfigInterface $scopeConfig,
        $name = 'VAT',
        $handlers = [],
         ?DateTimeZone $timezone = null
    ) {
        parent::__construct($name, $handlers, []);
        $this->scopeConfig = $scopeConfig;
       $this->timezone = $timezone ?: new DateTimeZone(date_default_timezone_get() ?: 'UTC');
    }

    /**
     * @param \Magento\Framework\DataObject|\Magento\Customer\Model\Data\Address $address
     */
    public function debugAddress($address)
    {
        if (is_object($address)) {
            $debug = [
                'id' => $address->getId(),
                'customer_id' => $address->getCustomerId(),
                'country_id' => $address->getCountryId(),
                'zip' => $address->getPostcode(),
                'vat_id' => $address->getVatId()
            ];
            $this->debug("address (".get_class($address)."): ".var_export($debug, true));
        }
    }

    /**
     * Adds a log record.
     *
     * @param  integer $level   The logging level
     * @param  string  $message The log message
     * @param  array   $context The log context
     *
     * @return Boolean Whether the record has been processed
     * @throws \Exception
     */
    public function addRecord(int $level, string $message, array $context = [], \Monolog\DateTimeImmutable $datetime = null): bool
    {
        if ($this->scopeConfig->getValue('euvat/mod_info/debug', ScopeInterface::SCOPE_STORE) == true) {

            if (!$this->handlers) {
                $this->pushHandler(new StreamHandler('php://stderr', static::DEBUG));
            }

            $levelName = static::getLevelName($level);

            // check if any handler will handle this message so we can return early and save cycles
            $handlerKey = null;
            foreach ($this->handlers as $key => $handler) {
                if ($handler->isHandling(['level' => $level])) {
                    $handlerKey = $key;
                    break;
                }
            }

            if (null === $handlerKey) {
                return false;
            }

           

            $record = [
                'message' => (string) $message,
                'context' => $context,
                'level' => $level,
                'level_name' => $levelName,
                'channel' => $this->name,
                'datetime' => $datetime ?? new \Monolog\DateTimeImmutable($this->microsecondTimestamps, $this->timezone),
                'extra' => [],
            ];

            foreach ($this->processors as $processor) {
                $record = call_user_func($processor, $record);
            }
            while (isset($this->handlers[$handlerKey]) &&
                   false === $this->handlers[$handlerKey]->handle($record)) {
                $handlerKey++;
            }

            return true;
        }

        return false;
    }
}
