namespace Geissweb\Euvat\Model\Config\Source;

/**
 * Class EuCountries
 * @package Geissweb\Euvat\Model\Config\Source
 */
class EuCountries implements \Magento\Framework\Option\ArrayInterface
{
    /**
     * To get config values
     * @var \Geissweb\Euvat\Helper\Configuration
     */
    private $configHelper;

    /**
     * EuCountries constructor.
     *
     * @param \Geissweb\Euvat\Helper\Configuration $configHelper
     */
    public function __construct(
        \Geissweb\Euvat\Helper\Configuration $configHelper
    ) {
        $this->configHelper = $configHelper;
    }

    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return $this->configHelper->getEuCountriesOptionArray();
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return $this->configHelper->getEuCountries();
    }
}
