namespace Geissweb\Euvat\Ui\Component\Form\CustomerAddress;

use Magento\Framework\View\Element\UiComponentInterface;

/**
 * Class Fieldset
 * @package Geissweb\Euvat\Ui\Component\Form\CustomerAddress
 */
class Fieldset extends \Magento\Ui\Component\Form\Fieldset
{
    /**
     * @var \Geissweb\Euvat\Helper\Configuration
     */
    public $configHelper;

    /**
     * @var \Magento\Ui\Component\Form\FieldFactory
     */
    private $fieldFactory;

    /**
     * Fieldset constructor.
     *
     * @param \Magento\Framework\View\Element\UiComponent\ContextInterface $context
     * @param \Magento\Ui\Component\Form\FieldFactory                      $fieldFactory
     * @param \Geissweb\Euvat\Helper\Configuration                         $configHelper
     * @param array                                                        $components
     * @param array                                                        $data
     */
    public function __construct(
        \Magento\Framework\View\Element\UiComponent\ContextInterface $context,
        \Magento\Ui\Component\Form\FieldFactory $fieldFactory,
        \Geissweb\Euvat\Helper\Configuration $configHelper,
        array $components = [],
        array $data = []
    ) {
        parent::__construct($context, $components, $data);
        $this->fieldFactory = $fieldFactory;
        $this->configHelper = $configHelper;
        $this->components = $components;
    }

    /**
     * Get components
     *
     * @return UiComponentInterface[]
     * @throws \Magento\Framework\Exception\NoSuchEntityException
     */
    public function getChildComponents()
    {
        if (!class_exists('\Magento\Customer\Ui\Component\Form\AddressFieldset')) {

            foreach ($this->components as $component) {
                if ($component->getName() === 'vat_id') {
                    $baseConfig = $component->getData('config');
                    //echo "<pre>";var_dump($baseConfig); echo "</pre>";
                    $additionalConfig = $this->configHelper->getAdminVatFieldConfig();
                    //echo "<pre>";var_dump($additionalConfig); echo "</pre>";
                    $finalConfig = array_merge($baseConfig, $additionalConfig);
                    //echo "<pre>";var_dump($finalConfig); echo "</pre>";die();
                    $component->setData('config', $finalConfig);
                }
            }

            $vatFields = [
                'vat_is_valid' => [
                    'label' => __('VAT number is valid'),
                    'formElement' => 'input',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 170
                ],
                'vat_request_success' => [
                    'label' => __('VAT validation request success'),
                    'formElement' => 'input',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 180
                ],
                'vat_request_id' => [
                    'label' => __('VAT validation request ID'),
                    'formElement' => 'input',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 190
                ],
                'vat_request_date' => [
                    'label' => __('VAT validation request date'),
                    'formElement' => 'input',
                    'dataType' => 'date',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 200
                ],
                'vat_trader_name' => [
                    'label' => __('Registered company name'),
                    'formElement' => 'input',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 210
                ],
                'vat_trader_address' => [
                    'label' => __('Registered company name'),
                    'formElement' => 'textarea',
                    'visible' => true,
                    'disabled' => true,
                    'source' => 'address',
                    'sortOrder' => 220
                ],
            ];

            foreach ($vatFields as $name => $fieldConfig) {
                $fieldInstance = $this->fieldFactory->create();
                $fieldInstance->setData(
                    [
                        'config' => $fieldConfig,
                        'name' => $name
                    ]
                );
                $fieldInstance->prepare();
                $this->addComponent($name, $fieldInstance);
            }
        }

        return parent::getChildComponents();
    }
}
