	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_WorldPay_Configuration $config){
		parent::__construct($paymentMethod);
		$this->globalConfiguration = $config;
	}

	public function getConfiguration(){
		return $this->globalConfiguration;
	}

	/**
	 *        	 			   		 	 	 
	 *
	 * @return Customweb_WorldPay_Configuration
	 */
	protected function getGlobalConfiguration(){
		return $this->globalConfiguration;
	}

	public function getPaymentMethodIdentifier(Customweb_WorldPay_Authorization_Transaction $transaction, $parameters){
		$params = $this->getPaymentMethodParameters();
		//if ($this->getConfiguration()->isInvisibleXMLAccessEnabled()) {
		if($transaction->getAuthorizationMethod() == Customweb_Payment_Authorization_PaymentPage_IAdapter::AUTHORIZATION_METHOD_NAME){
			return $params['PaymentMethod'];
		} else {
			return $params['PaymentMethodXML'];
		}
	}

	protected function getPaymentInformationMap(){
		return self::$paymentMapping;
	}

