	public function createXMLString($parameters, Customweb_Payment_Authorization_ITransaction $transaction){
		return null;
	}

	public function doRefund(Customweb_Payment_Authorization_ITransaction $transaction, Customweb_WorldPay_Configuration $configuration, $amount){
		return false;
	}

	public function checkYear($year){
		$stringLength = strlen((string) $year);
		if ($stringLength == 2) {
			return $year;
		} else {
			$year = substr($year, $stringLength - 2, $stringLength);
			return $year;
		}
	}

	public function processAuthorization(Customweb_Payment_Authorization_ITransaction $transaction, array $parameters , Customweb_WorldPay_Container $container){
		$this->secondOrderMessage($transaction, $parameters, $container);
		$this->finishBackend($transaction, $parameters, array(), $container);
		return $this->finalizeAuthorizationRequest($transaction, array());
	}

	protected function finishBackend(Customweb_Payment_Authorization_ITransaction $transaction, array $parameters, $xmlArray, Customweb_WorldPay_Container $container){
		$transaction->authorize(Customweb_I18n_Translation::__('Order successfully authorized via the WorldPay Server.'));
		if (!Customweb_WorldPay_Method_MethodHelper::isForcedByWorldpay($transaction)) {
			$paymentAction = Customweb_WorldPay_Method_MethodHelper::finishBackend($transaction, $this->getConfiguration());
			if ($paymentAction) {
				$captureAdapter = $container->getBean('Customweb_WorldPay_BackendOperation_Adapter_CaptureAdapter');
				try {
					if ($captureAdapter->directCapture($transaction)) {
						$transaction->capture();
					}
				} catch (Exception $e) {
					$transaction->addErrorMessage(new Customweb_Payment_Authorization_ErrorMessage(Customweb_I18n_Translation::__('Payment was not successful.'), Customweb_I18n_Translation::__('The payment was authorized, but could not be captured')));
				}
			}
		}
		else {
			$transaction->capture();
		}
	}

	protected function secondOrderMessage(Customweb_Payment_Authorization_ITransaction $transaction, array $parameters, Customweb_WorldPay_Container $container){
		$parameters['echo'] = $transaction->getEchoData();
		if (isset($parameters['MD'])) {
			$criticalData = json_decode($transaction->decrypt($parameters['MD']));
			
			$parameters = array_merge($parameters, $transaction->getCCDetails());
			if(isset($criticalData->cvv)) {
				$parameters['cvv'] = $criticalData->cvv;
			}
			$parameters['cardno'] = $criticalData->cardno;
		}
		$builder = new Customweb_WorldPay_Authorization_Server_ParameterBuilder($container, $transaction);
		$xmlString = $builder->generateXMLRequest($transaction, $parameters, $this, isset($parameters['PaRes']));
		
		$handler = Customweb_WorldPay_Util::sendDefaultRequest($xmlString, $this->getConfiguration(), $transaction->getCookies());
		$xmlArray = simplexml_load_string($handler->getBody());
		
		Customweb_WorldPay_Method_MethodHelper::validateInformation($transaction, $xmlArray, $this->getConfiguration());
	}

	public function finalizeAuthorizationRequest(Customweb_Payment_Authorization_ITransaction $transaction, array $parameters){
		if ($transaction->isAuthorizationFailed()) {
			return Customweb_Core_Http_Response::redirect($transaction->getFailedUrl());
		} else {
			return Customweb_Core_Http_Response::redirect($transaction->getSuccessUrl());
		}
	}
	
	public function requiresPaymentPageXmlRedirect() {
		return false;
	}
	
	public function getPaymentPageXmlRequest(Customweb_WorldPay_Container $container, Customweb_WorldPay_Authorization_Transaction $transaction, array $formData) {
		throw new Exception(Customweb_I18n_Translation::__('Not supported for this payment method'));
	}

